import React, { useState, useEffect } from 'react';
import './App.css';

const todoItems = [
  "Example of Racks A18 and A45 â€“ The racking should be in line with all the other racks and the steel beam should be inside the racks. Please do this for all areas where this is happening",
  "All Orange Racking Beams â€“ These look oval shaped when you look closely at them. They should be rectangular",
  "Battery Charging Area â€“ Please change wall colour to Light Grey",
  "Battery Charging Area â€“ Please change Door Colour to White",
  "Add Rack Labelling to each rack in the Mezzanine area â€“ Use M01, M02, M03 etc etc â€“ Please add labelling to both ends of the Mezzanine",
  "Mezzanine â€“ Insert all the orange beam levels",
  "Mezzanine â€“ Where have the walking floors gone? These need to be re-added",
  "All Doors directly accessing the warehouse from outside need to be considered a Fire Door and changed to red colour",
  'From inside the warehouse, please label above each Fire Door with a sign saying "Fire Exit"',
  "Safety side barrier on all of the outdoor ramps needs to be at least double the height",
  'Security â€“ Please add signage to side of security building "SECURITY" with Indu logo also applied to security office',
  "Security â€“ Add extra barrier system (See attached Image 1)",
  "External Dock Doors â€“ Please make them blue colour",
  "Toilet Doors â€“ Add Men/Women symbols",
  'Reception â€“ Please modernise the area. See attached "Reception" image as a kind of style guide',
  "Office Styling â€“ Use a similar modern style as the reception image and make the office look attractive. Bright and airy, Black framing, wooden features, nice light clearly tiled floors etc. Current office looks a bit grim ðŸ˜Š. If you need to add a few simple examples of furniture or different zones to make it look attractive then please do.",
  "Office â€“ Apply some Indu Logos in the right areas",
  "Office Toilets and Pantry â€“ Please make modern and attractive."
];

function App() {
  const [todos, setTodos] = useState(() => {
    // Get saved todos from localStorage or use default list
    const saved = localStorage.getItem('todos');
    if (saved) {
      return JSON.parse(saved);
    }
    // Initialize todos with completed: false
    return todoItems.map(item => ({
      text: item,
      completed: false
    }));
  });

  const [activeTab, setActiveTab] = useState('all');

  // Save todos to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem('todos', JSON.stringify(todos));
  }, [todos]);

  const handleToggle = (index) => {
    const newTodos = [...todos];
    newTodos[index].completed = !newTodos[index].completed;
    setTodos(newTodos);
  };

  const filteredTodos = todos.filter(todo => {
    if (activeTab === 'completed') return todo.completed;
    if (activeTab === 'pending') return !todo.completed;
    return true;
  });

  const getTabCount = (type) => {
    if (type === 'all') return todos.length;
    if (type === 'completed') return todos.filter(todo => todo.completed).length;
    if (type === 'pending') return todos.filter(todo => !todo.completed).length;
    return 0;
  };

  return (
    <div className="App">
      <h1>Warehouse Updates Checklist</h1>
      
      <div className="tabs">
        <button 
          className={`tab ${activeTab === 'all' ? 'active' : ''}`}
          onClick={() => setActiveTab('all')}
        >
          All Tasks ({getTabCount('all')})
        </button>
        <button 
          className={`tab ${activeTab === 'completed' ? 'active' : ''}`}
          onClick={() => setActiveTab('completed')}
        >
          Completed ({getTabCount('completed')})
        </button>
        <button 
          className={`tab ${activeTab === 'pending' ? 'active' : ''}`}
          onClick={() => setActiveTab('pending')}
        >
          Pending ({getTabCount('pending')})
        </button>
      </div>

      <div className="todo-list">
        {filteredTodos.map((todo, index) => (
          <div key={index} className="todo-item">
            <label className="checkbox-container">
              <input
                type="checkbox"
                checked={todo.completed}
                onChange={() => handleToggle(index)}
              />
              <span className="checkmark"></span>
              <span className={todo.completed ? 'completed' : ''}>
                {todo.text}
              </span>
            </label>
          </div>
        ))}
        {filteredTodos.length === 0 && (
          <div className="empty-state">
            No {activeTab} tasks found
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
