import React, { useState, useEffect } from 'react';
import './App.css';

const todoItems = [
  "Example of Racks A18 and A45 ‚Äì The racking should be in line with all the other racks and the steel beam should be inside the racks. Please do this for all areas where this is happening",
  "All Orange Racking Beams ‚Äì These look oval shaped when you look closely at them. They should be rectangular",
  "Battery Charging Area ‚Äì Please change wall colour to Light Grey",
  "Battery Charging Area ‚Äì Please change Door Colour to White",
  "Add Rack Labelling to each rack in the Mezzanine area ‚Äì Use M01, M02, M03 etc etc ‚Äì Please add labelling to both ends of the Mezzanine",
  "Mezzanine ‚Äì Insert all the orange beam levels",
  "Mezzanine ‚Äì Where have the walking floors gone? These need to be re-added",
  "All Doors directly accessing the warehouse from outside need to be considered a Fire Door and changed to red colour",
  'From inside the warehouse, please label above each Fire Door with a sign saying "Fire Exit"',
  "Safety side barrier on all of the outdoor ramps needs to be at least double the height",
  'Security ‚Äì Please add signage to side of security building "SECURITY" with Indu logo also applied to security office',
  "Security ‚Äì Add extra barrier system (See attached Image 1)",
  "External Dock Doors ‚Äì Please make them blue colour",
  "Toilet Doors ‚Äì Add Men/Women symbols",
  'Reception ‚Äì Please modernise the area. See attached "Reception" image as a kind of style guide',
  "Office Styling ‚Äì Use a similar modern style as the reception image and make the office look attractive. Bright and airy, Black framing, wooden features, nice light clearly tiled floors etc. Current office looks a bit grim üòä. If you need to add a few simple examples of furniture or different zones to make it look attractive then please do.",
  "Office ‚Äì Apply some Indu Logos in the right areas",
  "Office Toilets and Pantry ‚Äì Please make modern and attractive.",
  "Sky View ‚Äì Please add a sign saying 'Sky View' at the top of the stairs",
];

function App() {
  const [todos, setTodos] = useState(() => {
    // Get saved todos from localStorage or use default list
    const saved = localStorage.getItem('todos');
    if (saved) {
      return JSON.parse(saved);
    }
    // Initialize todos with completed: false
    return todoItems.map(item => ({
      text: item,
      completed: false
    }));
  });

  const [activeTab, setActiveTab] = useState('all');
  const [newTask, setNewTask] = useState('');
  const [editIndex, setEditIndex] = useState(null);
  const [editValue, setEditValue] = useState('');

  // Save todos to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem('todos', JSON.stringify(todos));
  }, [todos]);

  const handleToggle = (index) => {
    const newTodos = [...todos];
    newTodos[index].completed = !newTodos[index].completed;
    setTodos(newTodos);
  };

  const handleAddTask = (e) => {
    e.preventDefault();
    if (newTask.trim()) {
      setTodos([...todos, { text: newTask.trim(), completed: false }]);
      setNewTask('');
    }
  };

  const handleDelete = (index) => {
    const newTodos = todos.filter((_, i) => i !== index);
    setTodos(newTodos);
    if (editIndex === index) {
      setEditIndex(null);
      setEditValue('');
    }
  };

  const handleEdit = (index) => {
    setEditIndex(index);
    setEditValue(todos[index].text);
  };

  const handleEditChange = (e) => {
    setEditValue(e.target.value);
  };

  const handleEditSave = (index) => {
    if (editValue.trim()) {
      const newTodos = [...todos];
      newTodos[index].text = editValue.trim();
      setTodos(newTodos);
      setEditIndex(null);
      setEditValue('');
    }
  };

  const handleEditCancel = () => {
    setEditIndex(null);
    setEditValue('');
  };

  const filteredTodos = todos.filter(todo => {
    if (activeTab === 'completed') return todo.completed;
    if (activeTab === 'pending') return !todo.completed;
    return true;
  });

  const getTabCount = (type) => {
    if (type === 'all') return todos.length;
    if (type === 'completed') return todos.filter(todo => todo.completed).length;
    if (type === 'pending') return todos.filter(todo => !todo.completed).length;
    return 0;
  };

  return (
    <div className="App">
      <h1>Warehouse Updates Checklist</h1>

      <div className="tabs">
        <button 
          className={`tab ${activeTab === 'all' ? 'active' : ''}`}
          onClick={() => setActiveTab('all')}
        >
          All Tasks ({getTabCount('all')})
        </button>
        <button 
          className={`tab ${activeTab === 'completed' ? 'active' : ''}`}
          onClick={() => setActiveTab('completed')}
        >
          Completed ({getTabCount('completed')})
        </button>
        <button 
          className={`tab ${activeTab === 'pending' ? 'active' : ''}`}
          onClick={() => setActiveTab('pending')}
        >
          Pending ({getTabCount('pending')})
        </button>
      </div>

      <div className="todo-list">
        <form onSubmit={handleAddTask} className="add-task-form">
          <input
            type="text"
            value={newTask}
            onChange={(e) => setNewTask(e.target.value)}
            placeholder="Add a new task..."
            className="add-task-input"
          />
          <button type="submit" className="add-task-button">
            Add Task
          </button>
        </form>

        {filteredTodos.map((todo, idx) => {
          // Find the real index in the todos array
          const realIndex = todos.findIndex(t => t === todo);
          return (
            <div key={realIndex} className="todo-item">
              <label className="checkbox-container">
                <input
                  type="checkbox"
                  checked={todo.completed}
                  onChange={() => handleToggle(realIndex)}
                />
                <span className="checkmark"></span>
                {editIndex !== realIndex && (
                  <span className={todo.completed ? 'completed' : ''}>
                    {todo.text}
                  </span>
                )}
              </label>
              {editIndex === realIndex ? (
                <>
                  <textarea
                    className="edit-task-input"
                    value={editValue}
                    onChange={handleEditChange}
                    autoFocus
                    rows="3"
                  />
                  <div className="actions">
                    <button className="save-btn" onClick={() => handleEditSave(realIndex)} type="button">Save</button>
                    <button className="cancel-btn" onClick={handleEditCancel} type="button">Cancel</button>
                  </div>
                </>
              ) : (
                <div className="actions">
                  <button className="edit-btn" onClick={() => handleEdit(realIndex)} title="Edit" type="button">‚úèÔ∏è</button>
                  <button className="delete-btn" onClick={() => handleDelete(realIndex)} title="Delete" type="button">üóëÔ∏è</button>
                </div>
              )}
            </div>
          );
        })}
        {filteredTodos.length === 0 && (
          <div className="empty-state">
            No {activeTab} tasks found
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
